    public static void testPost( List params ,String uri){
        CloseableHttpAsyncClient httpclient = HttpAsyncClients.createDefault();
        try {
            HttpEntity   entity = new UrlEncodedFormEntity(params, "UTF-8");
        HttpPost requestPost = new HttpPost(uri);
        requestPost.setEntity(entity);

        httpclient.start();
        HttpResponse response = httpclient.execute(requestPost,null).get();
        HttpEntity entityResponse =response.getEntity();
        InputStream stream =entityResponse.getContent();
        System.out.println(UrlRequest.convertStreamToString(stream));
        httpclient.close();
        } catch (UnsupportedEncodingException e) {
            e.printStackTrace();
        } catch (InterruptedException e) {
            e.printStackTrace();
        } catch (ExecutionException e) {
            e.printStackTrace();
        } catch (IOException e) {
            e.printStackTrace();
        }
    }
